CREATE OR REPLACE FUNCTION obter_dados_banco_medico ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_resultado_w			varchar(60);
cd_agencia_bancaria_w		varchar(8);
nr_conta_w			varchar(25);
ds_banco_w			varchar(40);
cd_agencia_digito_w		varchar(10);
ie_tipo_conta_w			varchar(3);
cd_banco_w			banco.cd_banco%type;
/*
B - Banco
C - Conta/digito
A - Agencia
AD - Agencia/digito
TC - Tipo de conta
CO - CÃ³digo do banco
*/
BEGIN

select	coalesce(max(b.cd_agencia_bancaria),0),
	coalesce(max(b.nr_conta ||'-'|| b.nr_digito_conta),'0'),
	coalesce(max(a.ds_banco),''),
	coalesce(max(b.cd_agencia_bancaria ||'-'|| b.ie_digito_agencia),'0'),
	max(b.ie_tipo_conta),
	coalesce(max(a.cd_banco),0)
into STRICT	cd_agencia_bancaria_w,
	nr_conta_w,
	ds_banco_w,
	cd_agencia_digito_w,
	ie_tipo_conta_w,
	cd_banco_w
from	pessoa_fisica_conta b,
	banco a
where	b.cd_pessoa_fisica 	= cd_pessoa_fisica_p
and	a.cd_banco 		= b.cd_banco  LIMIT 1;

if (ie_opcao_p = 'B') then
	ds_resultado_w	:= ds_banco_w;
elsif (ie_opcao_p = 'C') then
	ds_resultado_w	:= nr_conta_w;
elsif (ie_opcao_p = 'A') then
	ds_resultado_w	:= cd_agencia_bancaria_w;
elsif (ie_opcao_p = 'AD') then
	ds_resultado_w 	:= cd_agencia_digito_w;
elsif (ie_opcao_p = 'TC') then
	ds_resultado_w 	:= ie_tipo_conta_w;
elsif (ie_opcao_p = 'CO') then
	ds_resultado_w 	:= cd_banco_w;
end if;

return ds_resultado_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
