CREATE OR REPLACE FUNCTION obter_dados_bco_ocorr_escr_ret ( nr_seq_ocorr_ret_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);


BEGIN


if (ie_opcao_p = 'C') then
	select	max(cd_ocorrencia)
	into STRICT	ds_retorno_w
	from	banco_ocorr_escrit_ret
	where	nr_sequencia	= nr_seq_ocorr_ret_p;
elsif (ie_opcao_p = 'D') then
	select	max(ds_ocorrencia)
	into STRICT	ds_retorno_w
	from	banco_ocorr_escrit_ret
	where	nr_sequencia	= nr_seq_ocorr_ret_p;
elsif (ie_opcao_p = 'I') then
	select	max(ie_rejeitado)
	into STRICT	ds_retorno_w
	from	banco_ocorr_escrit_ret
	where	nr_sequencia	= nr_seq_ocorr_ret_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
