CREATE OR REPLACE FUNCTION obter_dados_bloqueio_pac (nr_atendimento_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE


/*

1	Motivo_bloqueio
2	Observação
3	Usuário Bloqueio
4	Data Bloqueio

*/
nm_usuario_w	varchar(15);
dt_bloqueio_w	timestamp;
nr_seq_motivo_w	bigint;
ds_observacao_w	varchar(255);
ds_motivo_w	varchar(255);

C01 CURSOR FOR
	SELECT	nm_usuario,
		dt_atualizacao,
		nr_seq_motivo,
		ds_observacao
	from	bloqueio_titulo_paciente
	where	nr_atendimento = nr_atendimento_p
	and 	ie_bloqueio = 'B'
	order by nr_sequencia;


BEGIN

open C01;
loop
fetch C01 into
	nm_usuario_w,
	dt_bloqueio_w,
	nr_seq_motivo_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	nm_usuario_w	:=  nm_usuario_w;
	dt_bloqueio_w	:= dt_bloqueio_w;
	nr_seq_motivo_w	:= nr_seq_motivo_w;
	ds_observacao_w := ds_observacao_w;

	end;
end loop;
close C01;

if (ie_opcao_p = 1) then

	select 	max(ds_motivo_bloqueio)
	into STRICT	ds_motivo_w
	from 	motivo_bloqueio_titulo_pac
	where 	nr_sequencia = nr_seq_motivo_w;

	return	ds_motivo_w;

elsif (ie_opcao_p = 2) then

	return	ds_observacao_w;

elsif (ie_opcao_p = 3) then

	return	nm_usuario_w;

elsif (ie_opcao_p = 4) then

	return	dt_bloqueio_w;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
