CREATE OR REPLACE FUNCTION obter_dados_bordero_convenio ( nr_seq_conv_receb_p bigint, nr_bordero_rec_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(1) := 'N';
qt_convenio_w	bigint;
dt_recebimento	timestamp;


BEGIN

select	max(a.dt_recebimento)
into STRICT	dt_recebimento
from	bordero_recebimento a
where	a.nr_bordero   = nr_bordero_rec_p;

select	count(*)
into STRICT	qt_convenio_w
from	convenio_ret_receb a
where	a.nr_seq_receb = nr_seq_conv_receb_p;

if (dt_recebimento IS NOT NULL AND dt_recebimento::text <> '') and (qt_convenio_w = 0) then
	begin
	ds_retorno_w := 'S';
	end;
end if;

return	ds_retorno_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
