SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_bordero_tit_pagar ( nr_sequencia_p bigint, nr_bordero_p bigint, nr_titulo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


/*'
retorno:
VCI:	valor borderô com impostos
VSI:	valor borderô sem impostos
VT:	valor de impostos
VLI:	valor líquido com impostos
'*/
nr_sequencia_w		bigint;
nr_bordero_w		bigint;
nr_titulo_w		bigint;
vl_bordero_w		double precision;
vl_imposto_w		double precision;
vl_imposto_baixa_w		double precision;
vl_desconto_bordero_w	double precision;
vl_juros_bordero_w		double precision;
vl_multa_bordero_w		double precision;


ie_trib_atualiza_saldo_w	varchar(1);

ds_retorno_w		varchar(255);


BEGIN

begin
select	nr_sequencia
into STRICT	nr_sequencia_w
from	bordero_tit_pagar
where	nr_sequencia	= nr_sequencia_p;
exception
when others then
	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	bordero_tit_pagar
	where	nr_bordero = nr_bordero_p
	and	nr_titulo = nr_titulo_p;
end;

if (nr_sequencia_w > 0) then
	begin
	select	nr_bordero,
		nr_titulo,
		vl_bordero,
		coalesce(vl_desconto_bordero,0),
		coalesce(vl_juros_bordero,0),
		coalesce(vl_multa_bordero,0)
	into STRICT	nr_bordero_w,
		nr_titulo_w,
		vl_bordero_w,
		vl_desconto_bordero_w,
		vl_juros_bordero_w,
		vl_multa_bordero_w
	from	bordero_tit_pagar
	where	nr_sequencia	= nr_sequencia_w;
	exception
	when others then
		nr_bordero_w	:= null;
	end;
else
	begin
	select	nr_bordero,
		nr_titulo,
		vl_bordero,
		coalesce(vl_desconto_bordero,0),
		coalesce(vl_juros_bordero,0),
		coalesce(vl_multa_bordero,0)
	into STRICT	nr_bordero_w,
		nr_titulo_w,
		vl_bordero_w,
		vl_desconto_bordero_w,
		vl_juros_bordero_w,
		vl_multa_bordero_w
	from	titulo_pagar
	where	nr_bordero = nr_bordero_p
	and	nr_titulo = nr_titulo_p;
	exception
	when others then
		nr_bordero_w := null;
	end;
end if;

if (nr_bordero_w IS NOT NULL AND nr_bordero_w::text <> '') then
	begin
	select	coalesce(max(b.ie_trib_atualiza_saldo),'S')
	into STRICT	ie_trib_atualiza_saldo_w
	from	parametros_contas_pagar b,
		titulo_pagar a
	where	a.cd_estabelecimento	= b.cd_estabelecimento
	and	a.nr_titulo		= nr_titulo_w;

	vl_imposto_baixa_w	:= 0;
	vl_imposto_w		:= 0;

	if (ie_trib_atualiza_saldo_w = 'S') then
		begin
		if (ie_opcao_p in ('VSI', 'VT')) then
			vl_imposto_w	:= coalesce(obter_valores_tit_pagar(nr_titulo_w, clock_timestamp(), 'VT'),0);
		end if;

		if (ie_opcao_p in ('VSI', 'VCI', 'VLI')) then
			select	coalesce(sum(b.vl_imposto), 0)
			into STRICT	vl_imposto_baixa_w
			from	titulo_pagar_imposto b,
				titulo_pagar_baixa a
			where	a.nr_titulo	= b.nr_titulo
			and	a.nr_sequencia	= b.nr_seq_baixa
			and	b.ie_pago_prev	= 'V'
			and	a.nr_bordero	= nr_bordero_w
			and	a.nr_titulo	= nr_titulo_w;
		end if;
		end;
	end if;

	if (ie_opcao_p = 'VSI') then
		ds_retorno_w	:= vl_bordero_w + (vl_imposto_w - vl_imposto_baixa_w);
	elsif (ie_opcao_p = 'VCI') then
		ds_retorno_w	:= vl_bordero_w - vl_imposto_baixa_w;
	elsif (ie_opcao_p = 'VT') then
		ds_retorno_w	:= vl_imposto_w;
	elsif (ie_opcao_p = 'VLI') then
		ds_retorno_w	:= vl_bordero_w - vl_imposto_baixa_w + vl_juros_bordero_w - vl_desconto_bordero_w + vl_multa_bordero_w;
	end if;
	end;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_bordero_tit_pagar ( nr_sequencia_p bigint, nr_bordero_p bigint, nr_titulo_p bigint, ie_opcao_p text) FROM PUBLIC;

