CREATE OR REPLACE FUNCTION obter_dados_bordero_tit_rec ( nr_bordero_p bigint, nr_titulo_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE

		
/*'
retorno:
VC:	valor calculado
'*/
vl_calculado_w	        double precision;
ds_retorno_w	        varchar(255);
ie_acresc_bordero_w		parametro_contas_receber.ie_acresc_bordero%type;
cd_estabelecimento_w    integer;



BEGIN

	begin

	cd_estabelecimento_w := obter_estabelecimento_ativo();

	select	coalesce(max(a.ie_acresc_bordero),'S')
	into STRICT	ie_acresc_bordero_w
	from	parametro_contas_receber a
	where	a.cd_estabelecimento	= cd_estabelecimento_w;

	select	coalesce(sum(a.vl_abaixar),0) + CASE WHEN ie_acresc_bordero_w='N' THEN 0  ELSE coalesce(sum(a.vl_juros),0) + coalesce(sum(a.vl_multa),0) + coalesce(sum(a.vl_rec_maior),0) END
	into STRICT	vl_calculado_w
	from	bordero_tit_rec a
	where	a.nr_bordero	= nr_bordero_p
	and	    a.nr_titulo	= nr_titulo_p;

	exception
	when others then
		ds_retorno_w := null;
	end;

if (ie_opcao_p = 'VC') then
	ds_retorno_w := vl_calculado_w;
end if;

return	ds_retorno_w;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
