SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_brasindice (CD_ESTABELECIMENTO_P bigint, CD_MATERIAL_P bigint, DT_VIGENCIA_P timestamp, cd_convenio_p bigint, IE_OPCAO_P text) RETURNS varchar AS $body$
DECLARE


/* Opções Brasindice

	CLAB - Código do laboratório
	DLAB - Descrição do laboratório
	CMED - Código do medicamento
	DMED - Descrição do medicamento
	CAPR - Código da Apresentação
	DAPR - Descrição da Apresentação

*/
cd_laboratorio_w		varchar(06);
dt_vigencia_w			timestamp;
ds_laboratorio_w		varchar(40) 	:= '';
cd_convenio_w			bigint;
cd_medicamento_w		varchar(06);
ds_medicamento_w		varchar(80) 	:= '';
cd_apresentacao_w		varchar(6);
ds_apresentacao_w		varchar(155);
ds_expressao_w			varchar(40) 	:= '';


C01 CURSOR FOR
	SELECT	CD_LABORATORIO,
		cd_medicamento,
		cd_apresentacao
	FROM	MATERIAL_BRASINDICE
	WHERE	CD_MATERIAL						= CD_MATERIAL_P
	and	coalesce(cd_convenio, cd_convenio_w)				= cd_convenio_w
	and	coalesce(IE_SITUACAO, 'A')					= 'A'
	and (coalesce(dt_vigencia_w,clock_timestamp()) between
		coalesce(dt_vigencia,to_date('01/01/1900','dd/mm/yyyy')) and
		coalesce(dt_final_vigencia,to_date('31/12/2099','dd/mm/yyyy')))
	and	coalesce(cd_estabelecimento, coalesce(cd_estabelecimento_p, 0))	= coalesce(cd_estabelecimento_p, 0)
	order by coalesce(dt_vigencia,clock_timestamp() - interval '1000 days'),
		coalesce(cd_convenio,0);


BEGIN

DT_VIGENCIA_W 	:= coalesce(DT_VIGENCIA_P,clock_timestamp());
cd_convenio_w	:= coalesce(cd_convenio_p, 0);

OPEN C01;
LOOP
	FETCH C01 into
		cd_laboratorio_w,
		cd_medicamento_w,
		cd_apresentacao_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	cd_laboratorio_w	:= cd_laboratorio_w;
	cd_apresentacao_w	:= cd_apresentacao_w;
	cd_medicamento_w	:= cd_medicamento_w;
END LOOP;
CLOSE C01;


if (ie_opcao_p = 'CLAB') then
	Return cd_laboratorio_w;
elsif (ie_opcao_p = 'DLAB') then
	if (cd_laboratorio_w IS NOT NULL AND cd_laboratorio_w::text <> '') then
		ds_expressao_w := substr(OBTER_DESC_EXPRESSAO(729345),1,40);-- 729345: 'Laboratório não encontrado:'
		select coalesce(max(ds_laboratorio), ds_expressao_w || cd_laboratorio_w)
		into STRICT ds_laboratorio_w
		from brasindice_laboratorio
		where cd_laboratorio = cd_laboratorio_w;
	end if;
	Return ds_laboratorio_w;
elsif (ie_opcao_p = 'CMED') then
	Return cd_medicamento_w;
elsif (ie_opcao_p = 'DMED') then
	if (cd_medicamento_w IS NOT NULL AND cd_medicamento_w::text <> '') then
		ds_expressao_w := substr(OBTER_DESC_EXPRESSAO(729341),1,40);-- 729341: 'Medicamento não encontrado:'
		select coalesce(max(ds_medicamento), ds_expressao_w || cd_medicamento_w)
		into STRICT ds_medicamento_w
		from brasindice_medicamento
		where cd_medicamento 		= cd_medicamento_w;
	end if;
	Return ds_medicamento_w;
elsif (ie_opcao_p = 'CAPR') then
	Return cd_apresentacao_w;
elsif (ie_opcao_p = 'DAPR') then
	if (cd_apresentacao_w IS NOT NULL AND cd_apresentacao_w::text <> '') then
		ds_expressao_w := substr(OBTER_DESC_EXPRESSAO(729329),1,40);-- 729329: 'Apresentação não encontrada:'
		select coalesce(max(ds_apresentacao), ds_expressao_w || cd_apresentacao_w)
		into STRICT ds_apresentacao_w
		from brasindice_apresentacao
		where cd_apresentacao 		= cd_apresentacao_w;
	end if;
	Return ds_apresentacao_w;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
