SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_cancelamento ( nr_atendimento_p bigint, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

			
ds_obs_w		varchar(2000);
DS_MOTIVO_w	varchar(80);
nr_seq_canc_w	bigint;
ds_retorno_w		varchar(4000);
NR_SEQ_MOTIVO_w	bigint;			


BEGIN

select 	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_canc_w
from	atendimento_cancelamento
where	nr_atendimento	= nr_atendimento_p;

if (nr_seq_canc_w > 0) then
	
	select	DS_OBSERVACAO,
		coalesce(NR_SEQ_MOTIVO,0)
	into STRICT	ds_obs_w,
		NR_SEQ_MOTIVO_w
	from	atendimento_cancelamento
	where	nr_sequencia = nr_seq_canc_w;
	
	if (NR_SEQ_MOTIVO_w > 0) then
		
		select 	DS_MOTIVO
		into STRICT	DS_MOTIVO_w
		from	MOTIVO_CANCEL_ATEND
		where 	NR_SEQUENCIA = NR_SEQ_MOTIVO_w;	
	
	end if;
	

end if;

if (ie_opcao_p = 'OM') then
	
	if (DS_MOTIVO_w IS NOT NULL AND DS_MOTIVO_w::text <> '') then
		
		ds_retorno_w	:= substr(wheb_mensagem_pck.get_texto(308089) || ' ' || DS_MOTIVO_w || ' ' ,1,4000);
	
	end if;
	
	if (ds_obs_w IS NOT NULL AND ds_obs_w::text <> '') then
		ds_retorno_w := substr(ds_retorno_w || wheb_mensagem_pck.get_texto(83124) || ': ' || ds_obs_w,1,4000);
	
	end if;

elsif (ie_opcao_p = 'M') then
	
	if (DS_MOTIVO_w IS NOT NULL AND DS_MOTIVO_w::text <> '') then
		
		ds_retorno_w	:= substr(wheb_mensagem_pck.get_texto(308089) || ' ' || DS_MOTIVO_w || ' ',1,4000);
	
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
