CREATE OR REPLACE FUNCTION obter_dados_capa_ficha_financ (nr_sequencia_p bigint, ie_tipo_dado_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_tipo_dado
L - Descrição da linha
C- Descrição da Coluna
F- Descrição da F´rmula
*/
ds_retorno_w	varchar(255);


BEGIN
if (ie_tipo_dado_p = 'L') then
	select	ds_linha
	into STRICT	ds_retorno_w
	from	capa_ficha_financ_lin
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_tipo_dado_p = 'C') then
	select	ds_coluna
	into STRICT	ds_retorno_w
	from	capa_ficha_financ_col
	where	nr_sequencia = nr_sequencia_p;
elsif (ie_tipo_dado_p = 'F') then
	select	nm_formula
	into STRICT	ds_retorno_w
	from	ficha_financ_formula
	where	nr_sequencia = nr_sequencia_p;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
