CREATE OR REPLACE FUNCTION obter_dados_carga_tuss (nr_seq_tuss_mat_item_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_ref_fabricante_w	tuss_material_item.cd_ref_fabricante%type;
ds_fabricante_w		tuss_material_item.ds_fabricante%type;


BEGIN

ds_retorno_w:= '';

if (nr_seq_tuss_mat_item_p > 0) then

	select	max(cd_ref_fabricante),
		max(ds_fabricante)
	into STRICT	cd_ref_fabricante_w,
		ds_fabricante_w
	from	tuss_material_item
	where 	nr_sequencia = nr_seq_tuss_mat_item_p;

	if (ie_opcao_p = 'CRF') then
		ds_retorno_w:= substr(cd_ref_fabricante_w,1,255);
	elsif (ie_opcao_p = 'DF') then
		ds_retorno_w:= substr(ds_fabricante_w,1,255);
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
