SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_carta ( nr_atendimento_p bigint, cd_convenio_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE



/*
A - Autorizado
D - Deduzivel
C - Coseguro
*/
qt_registros_w		integer;
vl_retorno_w		double precision;
vl_autorizado_w		double precision;
vl_deduzivel_w		double precision;
pr_coseguro_hosp_w	double precision;

nr_sequencia_autor_w	bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	autorizacao_convenio a,
		estagio_autorizacao b
	where	a.nr_seq_estagio = b.nr_sequencia
	and	b.ie_interno not in ('70','90')  --NÃ£o contabiliza cancelados nem reprovados
	and 	a.cd_convenio = cd_convenio_p
	and	a.nr_atendimento = nr_atendimento_p;



c02 CURSOR FOR
SELECT	coalesce(PR_COSEGURO_HOSP,0),
	coalesce(VL_AUTORIZADO,0),
	coalesce(VL_DEDUZIVEL,0)
from	autorizacao_convenio_compl
where	nr_sequencia_autor = nr_sequencia_autor_w
and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
and	coalesce(dt_cancelamento::text, '') = ''
and	ie_tipo = 'S'
order by nr_sequencia;


BEGIN

vl_retorno_w	:= 0;

open C01;
loop
fetch C01 into
	nr_sequencia_autor_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	count(*)
	into STRICT	qt_registros_w
	from	autorizacao_convenio_compl
	where	nr_sequencia_autor = nr_sequencia_autor_w
	and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
	and	coalesce(dt_cancelamento::text, '') = ''
	and	ie_tipo = 'S';

	if (qt_registros_w = 0) then
		begin
		select	coalesce(sum(vl_autorizado),0),
			coalesce(SUM(pr_coseguro_hosp),0),
			coalesce(SUM(VL_DEDUZIVEL),0)
		into STRICT	vl_autorizado_w,
			pr_coseguro_hosp_w,
			vl_deduzivel_w
		from	autorizacao_convenio_compl
		where	nr_sequencia_autor = nr_sequencia_autor_w
		and	(dt_liberacao IS NOT NULL AND dt_liberacao::text <> '')
		and	coalesce(dt_cancelamento::text, '') = ''
		and	ie_tipo = 'C';
		end;
	else
		begin
		open c02;
		loop
		fetch	c02
		into	pr_coseguro_hosp_w,
			vl_autorizado_w,
			vl_deduzivel_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			pr_coseguro_hosp_w := pr_coseguro_hosp_w;
			vl_autorizado_w    := vl_autorizado_w;
			vl_deduzivel_w	   := vl_deduzivel_w;
		end loop;
		close c02;
		end;
	end if;

	if (ie_opcao_p = 'A') then
		vl_retorno_w := vl_retorno_w + vl_autorizado_w;
	elsif (ie_opcao_p = 'D') then
		vl_retorno_w := vl_retorno_w + vl_deduzivel_w;
	elsif (ie_opcao_p = 'C') then
		vl_retorno_w := vl_retorno_w + pr_coseguro_hosp_w;
	end if;


	end;
end loop;
close C01;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
