CREATE OR REPLACE FUNCTION obter_dados_cat_cert_parto ( nr_seq_cat_cert_parto_p bigint, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ds_cert_parto_w			cat_cert_parto.ds_cert_parto%type;
cd_cert_parto_mf_w		cat_cert_parto.cd_cert_parto_mf%type;
cd_cert_parto_w			cat_cert_parto.cd_cert_parto%type;



BEGIN
if (nr_seq_cat_cert_parto_p IS NOT NULL AND nr_seq_cat_cert_parto_p::text <> '') then

	select	ds_cert_parto,
		cd_cert_parto_mf,
		cd_cert_parto
	into STRICT	ds_cert_parto_w,
		cd_cert_parto_mf_w,
		cd_cert_parto_w
	from	cat_cert_parto
	where	nr_sequencia	= nr_seq_cat_cert_parto_p;

	if (ie_tipo_inf_p = 'DS_CERT_PARTO') then
		ds_retorno_w 	:= ds_cert_parto_w;

	elsif (ie_tipo_inf_p = 'CD_CERT_PARTO_MF') then
		ds_retorno_w 	:= cd_cert_parto_mf_w;

	elsif (ie_tipo_inf_p = 'CD_CERT_PARTO') then
		ds_retorno_w 	:= cd_cert_parto_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
