CREATE OR REPLACE FUNCTION obter_dados_cat_entidade ( cd_pessoa_fisica_p bigint, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_entidade_w			cat_entidade.cd_entidade%type;
ds_entidade_w			cat_entidade.ds_entidade%type;
ds_abreviacao_entidade_w	cat_entidade.ds_abreviacao_entidade%type;



BEGIN
if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	select	cd_entidade,
		ds_entidade,
		ds_abreviacao_entidade
	into STRICT	cd_entidade_w,
		ds_entidade_w,
		ds_abreviacao_entidade_w
	from 	cat_entidade ce, pessoa_fisica pf_p
	where 	pf_p.cd_pessoa_fisica = cd_pessoa_fisica_p
	and 	ce.vl_dominio = pf_p.sg_estado_nasc;

	if (ie_tipo_inf_p = 'CD_ENTIDADE') then
		ds_retorno_w 	:= cd_entidade_w;

	elsif (ie_tipo_inf_p = 'DS_ENTIDADE') then
		ds_retorno_w 	:= ds_entidade_w;

	elsif (ie_tipo_inf_p = 'DS_ABREVIACAO_ENTIDADE') then
		ds_retorno_w 	:= ds_abreviacao_entidade_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
