CREATE OR REPLACE FUNCTION obter_dados_cat_lugar_acc ( nr_seq_cat_lugar_acc_p bigint, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
cd_lugar_acc_w		cat_lugar_acc.cd_lugar_acc%type;
ds_lugar_acc_w		cat_lugar_acc.ds_lugar_acc%type;
cd_lugar_acc_mg_w	cat_lugar_acc.cd_lugar_acc_mg%type;


BEGIN
if (nr_seq_cat_lugar_acc_p IS NOT NULL AND nr_seq_cat_lugar_acc_p::text <> '') then

	select	cd_lugar_acc,
		ds_lugar_acc,
		cd_lugar_acc_mg
	into STRICT	cd_lugar_acc_w,
		ds_lugar_acc_w,
		cd_lugar_acc_mg_w
	from	cat_lugar_acc
	where	nr_sequencia	= nr_seq_cat_lugar_acc_p;

	if (ie_tipo_inf_p = 'CD_LUGAR_ACC') then
		ds_retorno_w 	:= cd_lugar_acc_w;

	elsif (ie_tipo_inf_p = 'DS_LUGAR_ACC') then
		ds_retorno_w 	:= ds_lugar_acc_w;

	elsif (ie_tipo_inf_p = 'CD_LUGAR_ACC_MG') then
		ds_retorno_w 	:= cd_lugar_acc_mg_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
