CREATE OR REPLACE FUNCTION obter_dados_cat_sitios ( nr_seq_cat_sitios_p bigint, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
nm_sitio_w			cat_sitios.nm_sitio%type;
nm_curto_sitio_w		cat_sitios.nm_curto_sitio%type;
cd_sitio_w			cat_sitios.cd_sitio%type;



BEGIN
if (nr_seq_cat_sitios_p IS NOT NULL AND nr_seq_cat_sitios_p::text <> '') then
	select	nm_sitio,
		nm_curto_sitio,
		cd_sitio
	into STRICT	nm_sitio_w,
		nm_curto_sitio_w,
		cd_sitio_w
	from	cat_sitios
	where	nr_sequencia	= nr_seq_cat_sitios_p;

	if (ie_tipo_inf_p = 'NM_SITIO') then
		ds_retorno_w 	:= nm_sitio_w;

	elsif (ie_tipo_inf_p = 'NM_CURTO_SITIO') then
		ds_retorno_w 	:= nm_curto_sitio_w;

	elsif (ie_tipo_inf_p = 'CD_SITIO') then
		ds_retorno_w 	:= cd_sitio_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
