CREATE OR REPLACE FUNCTION obter_dados_cat_tipo_assent ( nr_seq_cat_tipo_assent_p bigint, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
cd_tipo_asen_w			cat_tipo_assentamento.cd_tipo_asen%type;
nm_assentamento_w		cat_tipo_assentamento.nm_assentamento%type;



BEGIN
if (nr_seq_cat_tipo_assent_p IS NOT NULL AND nr_seq_cat_tipo_assent_p::text <> '') then

	select	cd_tipo_asen,
		nm_assentamento
	into STRICT	cd_tipo_asen_w,
		nm_assentamento_w
	from	cat_tipo_assentamento
	where	nr_sequencia	= nr_seq_cat_tipo_assent_p;

	if (ie_tipo_inf_p = 'CD_TIPO_ASEN') then
		ds_retorno_w 	:= cd_tipo_asen_w;

	elsif (ie_tipo_inf_p = 'NM_ASSENTAMENTO') then
		ds_retorno_w 	:= nm_assentamento_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
