CREATE OR REPLACE FUNCTION obter_dados_cat_tipo_via ( nr_seq_cat_tipo_via_p bigint, ie_tipo_inf_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255);
ds_tipo_via_w			cat_tipo_via.ds_tipo_via%TYPE;
cd_tipo_via_w			cat_tipo_via.cd_tipo_via%TYPE;



BEGIN
IF (nr_seq_cat_tipo_via_p IS NOT NULL AND nr_seq_cat_tipo_via_p::text <> '') THEN

	SELECT	ds_tipo_via,
		cd_tipo_via
	INTO STRICT	ds_tipo_via_w,
		cd_tipo_via_w
	FROM	cat_tipo_via
	WHERE	nr_sequencia	= nr_seq_cat_tipo_via_p;

	IF (ie_tipo_inf_p = 'DS_TIPO_VIA') THEN
		ds_retorno_w 	:= ds_tipo_via_w;

	ELSIF (ie_tipo_inf_p = 'CD_TIPO_VIA') THEN
		ds_retorno_w 	:= cd_tipo_via_w;
	END IF;
END IF;

RETURN	ds_retorno_w;

END;
