SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_checkup ( nr_seq_checkup_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/* ie_opcao_p 
ie_retorno		= Se há registro de Retorno 
ds_setor		= Setor do Paciente 
nm_paciente	= Nome do Paciente 
nm_empresa		= Nome da Empresa do Paciente 
nr_atendimento	= Número do Atendimento do Checkup 
*/
 
 
ds_retorno	varchar(255) := '';


BEGIN 
 
if (ie_opcao_p = 'ie_retorno') then 
	select	coalesce(max('S'),'N') 
	into STRICT	ds_retorno 
	from	checkup_retorno 
	where	nr_seq_checkup = nr_seq_checkup_p;
	 
elsif (ie_opcao_p = 'ds_setor') then 
	select	obter_nome_setor(cd_setor_atendimento) 
	into STRICT	ds_retorno 
	from	checkup 
	where	nr_sequencia = nr_seq_checkup_p;
 
elsif (ie_opcao_p = 'nm_paciente') then 
	select	obter_nome_pf(cd_pessoa_fisica) 
	into STRICT	ds_retorno 
	from	checkup 
	where	nr_sequencia = nr_seq_checkup_p;
 
elsif (ie_opcao_p = 'nm_empresa') then 
	select	obter_nome_empresa_ref(cd_empresa_ref) 
	into STRICT	ds_retorno 
	from	checkup 
	where	nr_sequencia = nr_seq_checkup_p;
 
elsif (ie_opcao_p = 'nr_atendimento') then 
	select	nr_atendimento 
	into STRICT	ds_retorno 
	from	checkup 
	where	nr_sequencia = nr_seq_checkup_p;
 
end if;
 
return	ds_retorno;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
