CREATE OR REPLACE FUNCTION obter_dados_ciclo_gpt (nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE


nr_ciclo_w		paciente_atendimento.nr_ciclo%type;
ds_dia_ciclo_w	paciente_atendimento.ds_dia_ciclo%type;
ds_retorno_w	varchar(255);


BEGIN

select	max(nr_ciclo),
		max(ds_dia_ciclo)
into STRICT	nr_ciclo_w,
		ds_dia_ciclo_w
from	paciente_atendimento
where	nr_prescricao = nr_prescricao_p;

if (nr_ciclo_w > 0) then
	ds_retorno_w := obter_desc_expressao(619521)||' '||nr_ciclo_w||' '||obter_desc_expressao(287659)||': '||ds_dia_ciclo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
