CREATE OR REPLACE FUNCTION obter_dados_ciclo ( nr_sequencia_p bigint, ie_retorno_p text) RETURNS varchar AS $body$
DECLARE


ds_equipamento_w	varchar(255);
ds_metodo_w		varchar(255);
ds_tipo_teste_w		varchar(255);
nr_ciclo_w		bigint;

/*
ie_retorno_p
E - Equipamento
M - MÃ©todo
T - Teste
C - Nr ciclo
*/
BEGIN

select	substr(cme_obter_desc_equip(nr_seq_equipamento),1,100) ds_equipamento,
	substr(cm_obter_metodo_ciclo(nr_sequencia),1,255) ds_metodo,
	nr_ciclo
into STRICT	ds_equipamento_w,
	ds_metodo_w,
	nr_ciclo_w
from	cm_ciclo
where	nr_sequencia = nr_sequencia_p;

select	max(substr(ds_tipo_teste,1,200))
into STRICT	ds_tipo_teste_w
from	cm_ciclo_tipo_teste b,
	cm_ciclo_teste a
where	a.nr_seq_teste = b.nr_sequencia
and	nr_seq_ciclo = nr_sequencia_p;

if (ie_retorno_p = 'E') then
	return	ds_equipamento_w;
elsif (ie_retorno_p = 'M') then
	return	ds_metodo_w;
elsif (ie_retorno_p = 'T') then
	return	ds_tipo_teste_w;
elsif (ie_retorno_p = 'C') then
	return	nr_ciclo_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
