CREATE OR REPLACE FUNCTION obter_dados_cih_precaucao ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ie_amostra_w		varchar(1);
ie_motivo_w			varchar(1);
ie_varios_motivos_w	varchar(1);


BEGIN

select  coalesce(max(ie_amostra),'S') ie_amostra,
        coalesce(max(ie_motivo),'S') ie_motivo,
        coalesce(max(ie_varios_motivos),'S') ie_varios_motivos
into STRICT	ie_amostra_w,
		ie_motivo_w,
		ie_varios_motivos_w
from    cih_precaucao
where nr_sequencia = nr_sequencia_p;

if (ie_opcao_p = 'A') then
	return	ie_amostra_w;
elsif (ie_opcao_p = 'M') then
	return	ie_motivo_w;
elsif (ie_opcao_p = 'VM') then
	return	ie_varios_motivos_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
