SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_cirur_profilaxia ( nr_atendimento_p bigint, nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
Opção

Data cirurgia			'D'
Procedimento Principal	'P'
Número da cirurgia		'C'

*/
ds_retorno_w			varchar(255);
dt_atual_cirurgia_w		timestamp;
dt_prescricao_w			timestamp;
dt_inicio_real_w		timestamp;
ds_procedimento_w		varchar(255);
ds_procedimento_atual_w	varchar(255);
nr_cirurgia_w			bigint;
nr_cirurgia_atual_w		bigint;

C01 CURSOR FOR
	SELECT	substr(dt_inicio_real,1,110),
			substr(obter_exame_agenda(cd_procedimento_princ, ie_origem_proced, nr_seq_proc_interno),1,240),
			nr_cirurgia
	from	cirurgia
	where	nr_atendimento	= nr_atendimento_p
	and		IE_STATUS_CIRURGIA = '2'
	order by  	dt_inicio_real;



BEGIN

Select 	dt_prescricao
into STRICT	dt_prescricao_w
from	prescr_medica
where	nr_prescricao = nr_prescricao_p;


open C01;
loop
fetch C01 into
	dt_inicio_real_w,
	ds_procedimento_w,
	nr_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(dt_atual_cirurgia_w::text, '') = '') then

		dt_atual_cirurgia_w 	:= dt_inicio_real_w;
		ds_procedimento_atual_w := ds_procedimento_w;
		nr_cirurgia_atual_w 	:= nr_cirurgia_w;

	else

		if (abs(dt_inicio_real_w - dt_prescricao_w) <=	abs(dt_prescricao_w - dt_atual_cirurgia_w) ) then

			dt_atual_cirurgia_w 	:= dt_inicio_real_w;
			ds_procedimento_atual_w := ds_procedimento_w;
			nr_cirurgia_atual_w 	:= nr_cirurgia_w;

		end if;

	end if;

	end;
end loop;
close C01;

if (ie_opcao_p = 'D') then

	ds_retorno_w := To_char(dt_atual_cirurgia_w, 'dd/mm/yyyy');

elsif (ie_opcao_p = 'P') then

	ds_retorno_w := ds_procedimento_atual_w;

else

	ds_retorno_w := nr_cirurgia_atual_w;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
