CREATE OR REPLACE FUNCTION obter_dados_classif_escala ( nr_seq_classif_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* C - Celular */

ds_retorno_w	varchar(200);
nr_celular_w	varchar(40);


BEGIN

if (nr_seq_classif_p > 0) then
	select	max(nr_telefone_celular)
	into STRICT	nr_celular_w
	from	escala_diaria_adic_classif
	where	nr_sequencia	=	nr_seq_classif_p;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w	:= nr_celular_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
