CREATE OR REPLACE FUNCTION obter_dados_classif_lote_presc (nr_seq_classif_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_classificacao_w			varchar(80);
ds_retorno_w			varchar(400);
ie_classif_urgente_w		varchar(3);
ie_controlado_w			varchar(1);
ie_padronizado_w		varchar(1);


BEGIN

if (coalesce(nr_seq_classif_p,0) <> 0) then
	begin
	select	coalesce(max(ds_classificacao),''),
		coalesce(max(ie_classif_urgente),'N'),
		coalesce(max(ie_controlado),'N'),
		coalesce(max(ie_padronizado),'N')
	into STRICT	ds_classificacao_w,
		ie_classif_urgente_w,
		ie_controlado_w,
		ie_padronizado_w
	from	classif_lote_disp_far
	where	nr_sequencia	= nr_seq_classif_p;

	if (ie_opcao_p = 'DS') then
		ds_retorno_w	:= substr(ds_classificacao_w,1,80);
	elsif (ie_opcao_p = 'U') then
		ds_retorno_w	:= substr(ie_classif_urgente_w,1,3);
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w	:= substr(ie_controlado_w,1,1);
	elsif (ie_opcao_p = 'P') then
		ds_retorno_w	:= substr(ie_padronizado_w,1,1);
	end if;
	end;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
