CREATE OR REPLACE FUNCTION obter_dados_classif_niss ( nr_sequencia_p bigint, ie_opcao_p text ) RETURNS varchar AS $body$
DECLARE


ds_classif_setor_w	varchar(80);
ie_neonatal_w		varchar(5);


BEGIN

select	ds_classif_setor,
	ie_neonatal
into STRICT	ds_classif_setor_w,
	ie_neonatal_w
from	niss_classif_setor
where	nr_sequencia	=	nr_sequencia_p;

if (ie_opcao_p = 'D') then
	return	ds_classif_setor_w;
elsif (ie_opcao_p = 'T') then
	return	ie_neonatal_w;
end if;

end;
$body$
