CREATE OR REPLACE FUNCTION obter_dados_cnae (nr_seq_cnae_p bigint, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
cd_cnae_w	varchar(10);
id_cnae_w	varchar(255);
ds_cnae_w	varchar(255);


BEGIN
	select	coalesce(max(cd_cnae),''),
		coalesce(max(id_cnae),''),
		coalesce(max(ds_cnae),'')
	into STRICT	cd_cnae_w,
		id_cnae_w,
		ds_cnae_w
	from	pls_cnae
	where	nr_sequencia	= nr_seq_cnae_p;

if (ie_tipo_p = 'CD') then
	return	cd_cnae_w;
elsif (ie_tipo_p = 'DS') then
	return	ds_cnae_w;
elsif (ie_tipo_p = 'ID') then
	return	id_cnae_w;
else
	return 'Sem Informação';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
