SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_cobranca_escrit ( nr_seq_cobranca_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*	ie_opcao_p
		VC = Valor total da cobrança
		QT = Quantidade de títulos da cobrança
*/
ds_retorno_w	varchar(255);
vl_cobranca_w	double precision;
qt_cobranca_w	bigint;


BEGIN

select	sum(vl_cobranca),
	count(*)
into STRICT	vl_cobranca_w,
	qt_cobranca_w
from	titulo_receber_cobr
where	nr_seq_cobranca = nr_seq_cobranca_p;

if (ie_opcao_p = 'VC') then
	ds_retorno_w	:= to_char(vl_cobranca_w);
elsif (ie_opcao_p = 'QT') then
	ds_retorno_w	:= to_char(qt_cobranca_w);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
