SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_coleta_entrega (nr_prescricao_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

/*
ie_opcao_p :
	CC = Códico setor coleta
	DC = Descrição setor coleta
	CE = Código setor entrega
	DE = Descrição setor entrega
*/
ds_retorno_w	varchar(100);


BEGIN

if (ie_opcao_p = 'CE') then
	select	max(cd_setor_entrega)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao	=	nr_prescricao_p;
elsif (ie_opcao_p = 'DE') then
	select	substr(obter_nome_setor(max(cd_setor_entrega)),1,100)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao	=	nr_prescricao_p;
elsif (ie_opcao_p = 'CC') then
	select	max(cd_setor_coleta)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao	=	nr_prescricao_p;
elsif (ie_opcao_p = 'DC') then
	select	substr(obter_nome_setor(max(cd_setor_coleta)),1,100)
	into STRICT	ds_retorno_w
	from	prescr_procedimento
	where	nr_prescricao	=	nr_prescricao_p;
end if;

return	coalesce(ds_retorno_w,'0');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_coleta_entrega (nr_prescricao_p bigint, ie_opcao_p text) FROM PUBLIC;

