SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_compl_pf_pj (cd_cgc_p text, cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(4000);
nr_sequencia_w	bigint;	

/*ie_opcao
TEL = Telefone
DDD = DDD
CON = Pessoa contato
EMA = Email
ICT = Identificacao de complemento
*/
	

BEGIN

if (cd_cgc_p IS NOT NULL AND cd_cgc_p::text <> '') then

	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	pessoa_juridica_compl a
	where	a.cd_cgc = cd_cgc_p;
	
	if ( ie_opcao_p =  'TEL' ) then
		select	a.nr_telefone
		into STRICT	ds_retorno_w
		from	pessoa_juridica_compl a
		where	a.cd_cgc = cd_cgc_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'DDD' ) then
		select	a.nr_ddd_telefone
		into STRICT	ds_retorno_w
		from	pessoa_juridica_compl a
		where	a.cd_cgc = cd_cgc_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'CON' ) then
		select	a.nm_pessoa_contato
		into STRICT	ds_retorno_w
		from	pessoa_juridica_compl a
		where	a.cd_cgc = cd_cgc_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'END' ) then
		select (DS_ENDERECO || '  N: ' || NR_ENDERECO)
		into STRICT	ds_retorno_w
		from	pessoa_juridica_compl a
		where	a.cd_cgc = cd_cgc_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'EMA' ) then
		select	a.ds_email
		into STRICT	ds_retorno_w
		from	pessoa_juridica_compl a
		where	a.cd_cgc = cd_cgc_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'ICT' ) then
		select	a.ds_identific_complemento
		into STRICT	ds_retorno_w
		from	pessoa_juridica_compl a
		where	a.cd_cgc = cd_cgc_p
		and	a.nr_sequencia = nr_sequencia_w;
	end if;
elsif (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	
	select	max(a.nr_sequencia)
	into STRICT	nr_sequencia_w
	from	compl_pessoa_fisica a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_p;
	
	if ( ie_opcao_p =  'TEL' ) then
		select	a.nr_telefone
		into STRICT	ds_retorno_w
		from	compl_pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'DDD' ) then
		select	a.nr_ddd_telefone
		into STRICT	ds_retorno_w
		from	compl_pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'CON' ) then
		select	a.nm_contato
		into STRICT	ds_retorno_w
		from	compl_pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.nr_sequencia = nr_sequencia_w;
	elsif ( ie_opcao_p =  'EMA' ) then
		select	a.ds_email
		into STRICT	ds_retorno_w
		from	compl_pessoa_fisica a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_p
		and	a.nr_sequencia = nr_sequencia_w;
	end if;
	
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_compl_pf_pj (cd_cgc_p text, cd_pessoa_fisica_p text, ie_opcao_p text) FROM PUBLIC;

