CREATE OR REPLACE FUNCTION obter_dados_compl_pf (cd_pessoa_fisica_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

	if (ie_opcao_p = 'M') then

		select 	cd_municipio_ibge
		into STRICT	ds_retorno_w
		from   	compl_pessoa_fisica
		where  	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	ie_tipo_complemento = 1;

	elsif (ie_opcao_p = 'E') then

		select 	sg_estado
		into STRICT	ds_retorno_w
		from   	compl_pessoa_fisica
		where  	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	ie_tipo_complemento = 1;

	elsif (ie_opcao_p = 'R') then

		select 	to_char(nr_ramal)
		into STRICT	ds_retorno_w
		from   	compl_pessoa_fisica
		where  	cd_pessoa_fisica = cd_pessoa_fisica_p
		and	ie_tipo_complemento = 1;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
