CREATE OR REPLACE FUNCTION obter_dados_compl_pj_estab ( cd_cgc_p text, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(255);
nr_count_w		bigint := 0;
nr_seq_pessoa_endereco_w		pessoa_juridica_compl.nr_seq_pessoa_endereco%type;


BEGIN

select	count(1)
into STRICT	nr_count_w
from	pessoa_juridica_compl
where	ie_tipo_complemento = 1
and	cd_cgc = cd_cgc_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (nr_count_w > 0) THEN

	if (address_pck.get_address_feature_enabled = 'S') then
		select	nr_seq_pessoa_endereco
		into STRICT	nr_seq_pessoa_endereco_w
		from	pessoa_juridica_compl
		where	ie_tipo_complemento = 1
		and	cd_cgc = cd_cgc_p
		and	cd_estabelecimento = cd_estabelecimento_p;
	end if;

	if (upper(ie_opcao_p) = 'R') then

		if (address_pck.get_address_feature_enabled = 'S') and (nr_seq_pessoa_endereco_w IS NOT NULL AND nr_seq_pessoa_endereco_w::text <> '') then
				select	street
				into STRICT	ds_retorno_w
				from	table(address_pck.get_address_fields(nr_seq_pessoa_endereco_w));
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(ds_endereco)
			into STRICT	ds_retorno_w
			from	pessoa_juridica_compl
			where	ie_tipo_complemento = 1
			and	cd_cgc		= cd_cgc_p
			and	cd_estabelecimento		= cd_estabelecimento_p;
		end if;
	end if;

	if (upper(ie_opcao_p) = 'NR') then

		if (address_pck.get_address_feature_enabled = 'S') and (nr_seq_pessoa_endereco_w IS NOT NULL AND nr_seq_pessoa_endereco_w::text <> '') then
				select	house_number
				into STRICT	ds_retorno_w
				from	table(address_pck.get_address_fields(nr_seq_pessoa_endereco_w));
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(nr_endereco)
			into STRICT	ds_retorno_w
			from	pessoa_juridica_compl
			where	ie_tipo_complemento = 1
			and	cd_cgc		= cd_cgc_p
			and	cd_estabelecimento		= cd_estabelecimento_p;
		end if;
	end if;

	if (upper(ie_opcao_p) = 'B') then

		if (address_pck.get_address_feature_enabled = 'S') and (nr_seq_pessoa_endereco_w IS NOT NULL AND nr_seq_pessoa_endereco_w::text <> '') then
				select	neighborhood
				into STRICT	ds_retorno_w
				from	table(address_pck.get_address_fields(nr_seq_pessoa_endereco_w));
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(ds_bairro)
			into STRICT	ds_retorno_w
			from	pessoa_juridica_compl
			where	ie_tipo_complemento = 1
			and	cd_cgc		= cd_cgc_p
			and	cd_estabelecimento		= cd_estabelecimento_p;
		end if;

	end if;
	if (upper(ie_opcao_p) = 'CI') then

		if (address_pck.get_address_feature_enabled = 'S') and (nr_seq_pessoa_endereco_w IS NOT NULL AND nr_seq_pessoa_endereco_w::text <> '') then
				select	city
				into STRICT	ds_retorno_w
				from	table(address_pck.get_address_fields(nr_seq_pessoa_endereco_w));
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(ds_municipio)
			into STRICT	ds_retorno_w
			from	pessoa_juridica_compl
			where	ie_tipo_complemento = 1
			and	cd_cgc		= cd_cgc_p
			and	cd_estabelecimento		= cd_estabelecimento_p;
		end if;

	end if;
	if (upper(ie_opcao_p) = 'UF') then

		if (address_pck.get_address_feature_enabled = 'S') and (nr_seq_pessoa_endereco_w IS NOT NULL AND nr_seq_pessoa_endereco_w::text <> '') then
				select	stateName
				into STRICT	ds_retorno_w
				from	table(address_pck.get_address_fields(nr_seq_pessoa_endereco_w));
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(obter_valor_dominio(50,sg_estado))
			into STRICT	ds_retorno_w
			from	pessoa_juridica_compl
			where	ie_tipo_complemento = 1
			and	cd_cgc		= cd_cgc_p
			and	cd_estabelecimento		= cd_estabelecimento_p;
		end if;

	end if;
	if (upper(ie_opcao_p) = 'CEP') then

		if (address_pck.get_address_feature_enabled = 'S') and (nr_seq_pessoa_endereco_w IS NOT NULL AND nr_seq_pessoa_endereco_w::text <> '') then
				select	postalCode
				into STRICT	ds_retorno_w
				from	table(address_pck.get_address_fields(nr_seq_pessoa_endereco_w));
		end if;

		if (coalesce(ds_retorno_w::text, '') = '') then
			select	max(cd_cep)
			into STRICT	ds_retorno_w
			from	pessoa_juridica_compl
			where	ie_tipo_complemento = 1
			and	cd_cgc		= cd_cgc_p
			and	cd_estabelecimento		= cd_estabelecimento_p;
		end if;

	end if;
else
	ds_retorno_w := obter_dados_pf_pj(null, cd_cgc_p, ie_opcao_p);
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
