CREATE OR REPLACE FUNCTION obter_dados_compl_pj ( cd_cgc_p text, nr_seq_complemento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

					 
ds_retorno_w		varchar(255);
nr_seq_ident_cnes_w	bigint;


BEGIN 
if (upper(ie_opcao_p) = 'CNES') then 
	select	max(nr_seq_ident_cnes) 
	into STRICT	nr_seq_ident_cnes_w 
	from	pessoa_juridica_compl 
	where	nr_sequencia	= nr_seq_complemento_p 
	and	cd_cgc		= cd_cgc_p;
	 
	select	max(cd_cnes) 
	into STRICT	ds_retorno_w 
	from	cnes_identificacao 
	where	nr_sequencia	= nr_seq_ident_cnes_w;
end if;
 
return	ds_retorno_w;
 
end;
