SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_cons_diaria ( ie_opcao_p text, nr_seq_agenda_p bigint, cd_agenda_p bigint) RETURNS varchar AS $body$
DECLARE


/*

 C	- CATEGORIA
 A	- TIPO ACOMODAÇÃO
 
*/
ds_tipo_acomodacao_w	varchar(255);
ds_categoria_w			varchar(255);
retorno_w				varchar(255);


BEGIN

if (ie_opcao_p	= 'C') then
select substr((coalesce((select obter_categoria_convenio(cd_convenio,cd_categoria)
	from	agenda_consulta a
	where nr_sequencia = nr_seq_agenda_p
	and	a.cd_agenda = cd_agenda_p), (select obter_categoria_convenio(cd_convenio,cd_categoria)
	from	agenda_paciente a
	where nr_sequencia = nr_seq_agenda_p
	and	a.cd_agenda = cd_agenda_p))),1,240) ds_categoria
	into STRICT ds_categoria_w
	;
	retorno_w := ds_categoria_w;
elsif (ie_opcao_p	= 'A') then
select substr((coalesce((select obter_desc_tipo_acomod(cd_tipo_acomodacao)
	from	agenda_consulta a
	where nr_sequencia = nr_seq_agenda_p
	and	a.cd_agenda = cd_agenda_p), (select obter_desc_tipo_acomod(cd_tipo_acomodacao)
	from	agenda_paciente a
	where nr_sequencia = nr_seq_agenda_p
	and	a.cd_agenda = cd_agenda_p))),1,240) ds_tipo_acomodacao
	into STRICT ds_tipo_acomodacao_w
	;
	retorno_w := ds_tipo_acomodacao_w;
end if;

return retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
