CREATE OR REPLACE FUNCTION obter_dados_conta_contabil_ans ( cd_conta_contabil_p text, ie_versao_ans_p text, cd_empresa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(255)	:= '';
cd_plano_w			bigint;
cd_classificacao_w			varchar(40);
ds_conta_w			varchar(255);
ie_tipo_w				varchar(1);

/*
CD	- Plano - ANS
CL	- Classificação - ANS
DS	- Descrição
DSA	- Descricao apresentacao
TP 	- Tipo
*/
BEGIN


select	max(a.cd_plano)
into STRICT	cd_plano_w
from	conta_contabil_ans a
where	a.cd_conta_contabil	= cd_conta_contabil_p
and	a.ie_versao		= ie_versao_ans_p
and	a.cd_empresa		= cd_empresa_p;

select	max(a.cd_plano),
	max(a.cd_classificacao),
	max(a.ds_conta),
	max(a.ie_tipo)
into STRICT	cd_plano_w,
	cd_classificacao_w,
	ds_conta_w,
	ie_tipo_w
from	ctb_plano_ans a
where	a.cd_plano	= cd_plano_w
and	a.ie_versao	= ie_versao_ans_P
and	a.cd_empresa	= cd_empresa_p;


if (ie_opcao_p = 'CD') then

	ds_retorno_w	:= cd_plano_w;
elsif (ie_opcao_p = 'DS') then

	ds_retorno_w	:= ds_conta_w;
elsif (coalesce(cd_plano_w, 0) <> 0) and (ie_opcao_p = 'DSA') then

	ds_retorno_w	:= substr(cd_plano_w  || ' - ' || cd_classificacao_w || '  ' || ds_conta_w, 1, 255);
elsif (ie_opcao_p = 'CL') then

	ds_retorno_w	:= cd_classificacao_w;
elsif (ie_opcao_p = 'TP') then

	ds_retorno_w	:= ie_tipo_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
