SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_conta_contabil ( cd_conta_contabil_p text, cd_estabelecimento_p bigint, ie_resultado_p text) RETURNS varchar AS $body$
DECLARE


/*ie_resultado_p
CR = Criterio Rateio
CC = Centro Custo
CL = Classificacao
T  = Tipo (Analitica ou Título)
S  = Situacao
DC = Debito/Credito
TG = Tipo do grupo da conta (Resultado, Ativo, Passivo  dominio 1143)
SC = Código sistema contábil
G = Código do grupo da conta
*/
nr_seq_crit_conta_w	varchar(10);
nr_seq_crit_rateio_w	varchar(10);
cd_centro_custo_conta_w	varchar(8);
cd_centro_custo_w		varchar(8);
vl_resultado_w		varchar(255);
cd_classificacao_w		varchar(40);
ie_tipo_w			varchar(01);
ie_tipo_grupo_w		varchar(01);
ie_situacao_w		varchar(01);
cd_grupo_w		bigint;
ie_debito_credito_w		varchar(1);
cd_sistema_contabil_w	varchar(255);


BEGIN

select	max(nr_seq_crit_rateio),
	max(cd_centro_custo),
	max(cd_classificacao),
	max(ie_tipo),
	max(ie_situacao),
	max(cd_grupo),
	max(cd_sistema_contabil)
into STRICT	nr_seq_crit_conta_w,
	cd_centro_custo_conta_w,
	cd_classificacao_w,
	ie_tipo_w,
	ie_situacao_w,
	cd_grupo_w,
	cd_sistema_contabil_w
from	conta_contabil
where	cd_conta_contabil = cd_conta_contabil_p;

if (cd_grupo_w > 0) then
	select	coalesce(max(ie_tipo),'')
	into STRICT	ie_tipo_grupo_w
	from	ctb_grupo_conta
	where	cd_grupo = cd_grupo_w;
end if;

select	coalesce(max(nr_seq_crit_rateio), nr_seq_crit_conta_w),
	coalesce(max(cd_centro_custo), cd_centro_custo_conta_w)
into STRICT	nr_seq_crit_rateio_w,
	cd_centro_custo_w
from	conta_contabil_estab
where	cd_conta_contabil = cd_conta_contabil_p
and	cd_estabelecimento = cd_estabelecimento_p;

if (ie_resultado_p = 'CR') then
	vl_resultado_w	:= nr_seq_crit_rateio_w;
elsif (ie_resultado_p = 'CC') then
	vl_resultado_w	:= cd_centro_custo_w;
elsif (ie_resultado_p = 'CL') then
	vl_resultado_w	:= substr(ctb_obter_classif_conta(cd_conta_contabil_p, cd_classificacao_w, null),1,40);
elsif (ie_resultado_p = 'T') then
	vl_resultado_w	:= ie_tipo_w;
elsif (ie_resultado_p = 'S') then
	vl_resultado_w	:= ie_situacao_w;
elsif (ie_resultado_p = 'DC') and (cd_grupo_w > 0) then
	select	ie_debito_credito
	into STRICT	vl_resultado_w
	from	ctb_grupo_conta
	where	cd_grupo = cd_grupo_w;
elsif (ie_resultado_p = 'TG') then
	vl_resultado_w	:= ie_tipo_grupo_w;
elsif (ie_resultado_p = 'SC') then
	vl_resultado_w	:= cd_sistema_contabil_w;
elsif (ie_resultado_p = 'G') then
	vl_resultado_w	:= coalesce(cd_grupo_w,0);
end if;

return vl_resultado_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
