SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_conta_estab (nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*
DC	- Descrição completa da conta
CC	- Código Conta
CA	- Código Agência
DA	- Descrição Agência
CB	- Código Banco
DB	- Descrição Banco
*/
ds_retorno_w	varchar(255);


BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then

	if (ie_opcao_p	= 'DC') then
		select	ds_conta
		into STRICT	ds_retorno_w
		from	banco_estabelecimento_v
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p	= 'CC') then
		select	cd_conta||'-'||ie_digito_conta
		into STRICT	ds_retorno_w
		from	banco_estabelecimento_v
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p	= 'CA') then
		select	cd_agencia_bancaria||'-'||ie_digito_agencia
		into STRICT	ds_retorno_w
		from	banco_estabelecimento_v
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p	= 'DA') then
		select	ds_agencia_bancaria
		into STRICT	ds_retorno_w
		from	banco_estabelecimento_v
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p	= 'CB') then
		select	cd_banco
		into STRICT	ds_retorno_w
		from	banco_estabelecimento_v
		where	nr_sequencia	= nr_sequencia_p;
	elsif (ie_opcao_p	= 'DB') then
		select	ds_banco
		into STRICT	ds_retorno_w
		from	banco_estabelecimento_v
		where	nr_sequencia	= nr_sequencia_p;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
