SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_conta_guia_grg (nr_interno_conta_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(255);
cd_convenio_w		bigint;
cd_categoria_w		varchar(10);
nr_atendimento_w 	bigint;
ds_tipo_atendimento_w	varchar(200);
ds_plano_convenio_w	varchar(200);

/*ie_opcao_p 
DC - Descricao Categoria 
TA - Tipo atendimento 
PL - Plano atendimento 
*/
 
 

BEGIN 
 
begin 
select	substr(obter_valor_dominio(12,b.ie_tipo_atendimento),1,200) ds_tipo_atend, 
	substr(Obter_Desc_Plano(c.cd_convenio, c.cd_plano_convenio),1,200) ds_plano 
into STRICT	ds_tipo_atendimento_w, 
	ds_plano_convenio_w 
from	conta_paciente a, 
	atendimento_paciente b, 
	atend_categoria_convenio c 
where	a.nr_atendimento = b.nr_atendimento 
and	b.nr_atendimento = c.nr_atendimento 
and	c.nr_seq_interno = obter_atecaco_atendimento(c.nr_atendimento) 
and	a.nr_interno_conta = nr_interno_conta_p;
exception 
when others then 
	ds_tipo_atendimento_w := null;
	ds_plano_convenio_w  := null;
end;
 
if (ie_opcao_p = 'DC') then 
	 
	select	max(cd_categoria_parametro), 
		max(cd_convenio_parametro) 
	into STRICT	cd_categoria_w, 
		cd_convenio_w 
	from	conta_paciente 
	where	nr_interno_conta = nr_interno_conta_p;
	 
	select	max(ds_categoria) 
	into STRICT	ds_retorno_w 
	from	categoria_convenio 
	where	cd_convenio = cd_convenio_w 
	and	cd_categoria = cd_categoria_w;
elsif (ie_opcao_p = 'TA') then 
	ds_retorno_w := ds_tipo_atendimento_w;
elsif (ie_opcao_p = 'PL') then 
	ds_retorno_w := ds_plano_convenio_w;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
