CREATE OR REPLACE FUNCTION obter_dados_conta_item_autor (nr_seq_item_autor_p bigint, nr_prescricao_p bigint, nr_seq_prescricao_p bigint, ie_tipo_valor_p text, ie_proc_mat_p bigint) RETURNS bigint AS $body$
DECLARE

			 
vl_retorno_w		double precision;
nr_interno_conta_w	bigint;
vl_conta_w		double precision;

/* ie_tipo_valor_p 
C - conta 
V - Valor da conta 
 
ie_proc_mat_p 
1 - Procedimento 
2 - Material 
 
*/
 
			 

BEGIN 
if (ie_proc_mat_p = 1) then 
	begin 
	select	max(a.nr_interno_conta), 
		coalesce(max(obter_valor_conta(a.nr_interno_conta,0)),0) 
	into STRICT	nr_interno_conta_w, 
		vl_conta_w 
	from	procedimento_paciente a 
	where	((a.nr_seq_proc_autor = nr_seq_item_autor_p) or 
		(a.nr_sequencia_prescricao = nr_seq_prescricao_p AND a.nr_prescricao		  = nr_prescricao_p));
	 
	end;
elsif (ie_proc_mat_p = 2) then 
	begin 
	select	max(a.nr_interno_conta), 
		coalesce(max(obter_valor_conta(a.nr_interno_conta,0)),0) 
	into STRICT	nr_interno_conta_w, 
		vl_conta_w 
	from	material_atend_paciente a 
	where	((a.nr_seq_mat_autor = nr_seq_item_autor_p) or 
		(a.nr_sequencia_prescricao = nr_seq_prescricao_p AND a.nr_prescricao		  = nr_prescricao_p));
 
	end;
end if;
 
 
if (ie_tipo_valor_p = 'C') then 
	vl_retorno_w	:= nr_interno_conta_w;
elsif (ie_tipo_valor_p = 'V') then 
	vl_retorno_w 	:= vl_conta_w;
end if;
 
return	vl_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
