CREATE OR REPLACE FUNCTION obter_dados_conta_paciente_dt ( nr_interno_conta_p bigint, ie_opcao_p text) RETURNS timestamp AS $body$
DECLARE

 
/*ie_opcao_p 
 
DVNF	- Data de vencimento da NF - Usado no repasse para terceiros (repasses pendentes) - ahoffelder 05/10/2010 OS 253507 
DPI	- Período inicial - jcaraujo 27/10/10 - OS261902 
DPF	- Período final - jcaraujo 27/10/10 - OS261902 
DTA	- Data da alta do paciente 
DTC	- data de cancelamento null 
DR	- Data mês ano referência 
 
*/
 
 
dt_retorno_w		timestamp;

nr_atendimento_w		bigint;
dt_periodo_inicial_w	timestamp;
dt_periodo_final_w		timestamp;
dt_mesano_referencia_w	timestamp;


BEGIN 
 
select	max(dt_periodo_inicial), 
	max(dt_periodo_final), 
	max(dt_mesano_referencia), 
	max(nr_atendimento) 
into STRICT	dt_periodo_inicial_w, 
	dt_periodo_final_w, 
	dt_mesano_referencia_w, 
	nr_atendimento_w 
from	conta_paciente 
where	nr_interno_conta = nr_interno_conta_p;
 
if (ie_opcao_p = 'DVNF') then 
	select	min(b.dt_vencimento) 
	into STRICT	dt_retorno_w 
	from	nota_fiscal_venc b, 
		nota_fiscal a 
	where	a.nr_interno_conta	= nr_interno_conta_p 
	and	a.nr_sequencia	= b.nr_sequencia;
 
elsif (ie_opcao_p = 'DPI') then 
	dt_retorno_w	:= dt_periodo_inicial_w;
		 
elsif (ie_opcao_p = 'DPF') then 
	dt_retorno_w	:= dt_periodo_final_w;
	 
elsif (ie_opcao_p = 'DTA') then 
	select	dt_alta 
	into STRICT	dt_retorno_w 
	from	atendimento_paciente 
	where	nr_atendimento	= nr_atendimento_w;
		 
elsif (ie_opcao_p = 'DR') then 
	dt_retorno_w	:= dt_mesano_referencia_w;
	 
end if;
 
return	dt_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_conta_paciente_dt ( nr_interno_conta_p bigint, ie_opcao_p text) FROM PUBLIC;

