SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_conta_titulo_pagar ( nr_titulo_p bigint, cd_estabelecimento_p bigint, ie_tipo_dado_p text ) RETURNS varchar AS $body$
DECLARE


/* ie_tipo_dado_p
	B	:  cd_banco
	AB	: cd_agencia_bancaria
	DA 	: ie_digito_agencia
	C	: nr_conta
	DB	: ds_banco
	DC	: nr_digito_conta
	CI	: cd_codigo_identificacao
	O	: ds_observacao
	BAC	: cd_banco - cd_agencia_bancaria - nr_conta
	CD	: cd_conta - nr_digito_conta
	TC	: ie_tipo_conta
	DT	: descricao do ie_tipo_conta
*/
ds_retorno_w			varchar(255);
cd_pessoa_fisica_w		varchar(10);
cd_cgc_w			varchar(14);
nr_conta_titulo_w		varchar(20);

cd_banco_w			banco.cd_banco%type;
cd_agencia_bancaria_w		varchar(8);
ie_digito_agencia_w		varchar(2);
nr_conta_w			varchar(20);
ds_banco_w			varchar(40);
nr_digito_conta_w		varchar(2);
cd_codigo_identificacao_w	varchar(20);
ds_observacao_w			varchar(255);
ds_agencia_bancaria_w		varchar(40);
ie_tipo_conta_w			varchar(5);

c01 CURSOR FOR
	SELECT	a.cd_banco,
		a.cd_agencia_bancaria,
		a.ie_digito_agencia,
		a.nr_conta,
		b.ds_banco,
		a.nr_digito_conta,
		'' cd_codigo_identificacao,
		'' ds_observacao,
		a.ie_tipo_conta
	from	banco b,
		pessoa_fisica_conta a
	where	a.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	a.cd_banco = b.cd_banco
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	'S' = verifica_conta_estab(a.cd_banco, a.cd_agencia_bancaria, a.nr_conta, a.cd_pessoa_fisica, null, cd_estabelecimento_p)
	order	by a.ie_conta_pagamento desc, 1, 2;

c02 CURSOR FOR
	SELECT	a.cd_banco,
		a.cd_agencia_bancaria,
		a.ie_digito_agencia,
		a.nr_conta,
		b.ds_banco,
		a.nr_digito_conta,
		a.cd_codigo_identificacao,
		substr(a.ds_observacao,1,255),
		a.ie_tipo_conta
	from	banco b,
		pessoa_juridica_conta a
	where	a.cd_cgc = cd_cgc_w
	and	a.cd_banco = b.cd_banco
	and	coalesce(a.ie_situacao,'A') = 'A'
	and	'S' = verifica_conta_estab(a.cd_banco, a.cd_agencia_bancaria, a.nr_conta, null, a.cd_cgc, cd_estabelecimento_p)
	order	by a.ie_conta_pagamento desc, 1, 2;
	

BEGIN

select	max(cd_pessoa_fisica),
	max(cd_cgc),
	max(nr_conta)
into STRICT	cd_pessoa_fisica_w,
	cd_cgc_w,
	nr_conta_titulo_w
from	titulo_pagar
where	nr_titulo = nr_titulo_p;

if (nr_conta_titulo_w IS NOT NULL AND nr_conta_titulo_w::text <> '') then
	if (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
		select	max(a.cd_banco),
			max(a.cd_agencia_bancaria),
			max(a.ie_digito_agencia),
			max(a.nr_conta),
			max(b.ds_banco),
			max(a.nr_digito_conta),
			'' cd_codigo_identificacao,
			'' ds_observacao,
			max(a.ie_tipo_conta)
		into STRICT	cd_banco_w,
			cd_agencia_bancaria_w,
			ie_digito_agencia_w,
			nr_conta_w,
			ds_banco_w,
			nr_digito_conta_w,
			cd_codigo_identificacao_w,
			ds_observacao_w,
			ie_tipo_conta_w
		from	banco b,
			pessoa_fisica_conta a
		where	a.cd_pessoa_fisica = cd_pessoa_fisica_w
		and	a.nr_conta = nr_conta_titulo_w
		and	a.cd_banco = b.cd_banco;
	elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
		select	max(a.cd_banco),
			max(a.cd_agencia_bancaria),
			max(a.ie_digito_agencia),
			max(a.nr_conta),
			max(b.ds_banco),
			max(a.nr_digito_conta),
			max(a.cd_codigo_identificacao),
			substr(max(a.ds_observacao),1,255),
			max(a.ie_tipo_conta)
		into STRICT	cd_banco_w,
			cd_agencia_bancaria_w,
			ie_digito_agencia_w,
			nr_conta_w,
			ds_banco_w,
			nr_digito_conta_w,
			cd_codigo_identificacao_w,
			ds_observacao_w,
			ie_tipo_conta_w
		from	banco b,
			pessoa_juridica_conta a
		where	a.cd_cgc = cd_cgc_w
		and	a.nr_conta = nr_conta_titulo_w
		and	a.cd_banco = b.cd_banco;
	end if;
elsif (cd_pessoa_fisica_w IS NOT NULL AND cd_pessoa_fisica_w::text <> '') then
	open c01;
	fetch c01 into
		cd_banco_w,
		cd_agencia_bancaria_w,
		ie_digito_agencia_w,
		nr_conta_w,
		ds_banco_w,
		nr_digito_conta_w,
		cd_codigo_identificacao_w,
		ds_observacao_w,
		ie_tipo_conta_w;
	close c01;
elsif (cd_cgc_w IS NOT NULL AND cd_cgc_w::text <> '') then
	open c02;
	fetch c02 into
		cd_banco_w,
		cd_agencia_bancaria_w,
		ie_digito_agencia_w,
		nr_conta_w,
		ds_banco_w,
		nr_digito_conta_w,
		cd_codigo_identificacao_w,
		ds_observacao_w,
		ie_tipo_conta_w;
	close c02;
end if;

if (ie_tipo_dado_p = 'B') then
	ds_retorno_w := cd_banco_w;
elsif (ie_tipo_dado_p = 'AB') then
	ds_retorno_w := cd_agencia_bancaria_w;
elsif (ie_tipo_dado_p = 'DA') then
	ds_retorno_w := ie_digito_agencia_w;
elsif (ie_tipo_dado_p = 'C') then
	ds_retorno_w := nr_conta_w;
elsif (ie_tipo_dado_p = 'DB') then
	ds_retorno_w := ds_banco_w;
elsif (ie_tipo_dado_p = 'DC') then
	ds_retorno_w := nr_digito_conta_w;
elsif (ie_tipo_dado_p = 'CI') then
	ds_retorno_w := cd_codigo_identificacao_w;
elsif (ie_tipo_dado_p = 'O') then
	ds_retorno_w := ds_observacao_w;
elsif (ie_tipo_dado_p = 'BAC') then
	select	max(a.ds_agencia_bancaria)
	into STRICT	ds_agencia_bancaria_w
	from	agencia_bancaria a
	where	a.cd_agencia_bancaria	= cd_agencia_bancaria_w;
	
	ds_retorno_w := ds_banco_w || ' - ' ||
			coalesce(cd_agencia_bancaria_w, ds_agencia_bancaria_w) || '/' || ie_digito_agencia_w || ' - ' || 
			nr_conta_w || '/' || nr_digito_conta_w;
			
elsif (ie_tipo_dado_p = 'CD') then
	ds_retorno_w := nr_conta_w || '-' || nr_digito_conta_w;
elsif (ie_tipo_dado_p = 'TC') then
	ds_retorno_w := ie_tipo_conta_w;
elsif (ie_tipo_dado_p = 'DT') then
	case	ie_tipo_conta_w
		when	'CC'	then ds_retorno_w := Wheb_mensagem_pck.get_texto(303557);
		when	'CP'	then ds_retorno_w := Wheb_mensagem_pck.get_texto(303558);
		when 	'DJ'	then ds_retorno_w := Wheb_mensagem_pck.get_texto(303559);
		when	'CS'	then ds_retorno_w := Wheb_mensagem_pck.get_texto(303560);
		else	ds_retorno_w := '';
	end case;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_conta_titulo_pagar ( nr_titulo_p bigint, cd_estabelecimento_p bigint, ie_tipo_dado_p text ) FROM PUBLIC;

