CREATE OR REPLACE FUNCTION obter_dados_contrato_pessoa (nr_seq_controle_p bigint, ie_tipo_dados_p text) RETURNS varchar AS $body$
DECLARE


/*
TPF -> Total de pessoa física
TPJ -> Total de pessoa jurídica
TP    -> Total Geral de pessoas
DS   ->  Descrição do controle

*/
qt_registros_w	bigint;
ds_retorno_w	varchar(255);


BEGIN

if (ie_tipo_dados_p = 'TPF') then
	select  count(*)
	into STRICT	qt_registros_w
	from	TIPO_CONTROLE_PESSOA a,
		CONTROLE_PESSOA b
	where	a.cd_tipo_controle = b.cd_tipo_controle
	and	a.cd_tipo_controle = nr_seq_controle_p
	and	(b.cd_pessoa_fisica IS NOT NULL AND b.cd_pessoa_fisica::text <> '');

	ds_retorno_w := to_char(qt_registros_w);

elsif (ie_tipo_dados_p = 'TPJ') then
	select  count(*)
	into STRICT	qt_registros_w
	from	TIPO_CONTROLE_PESSOA a,
		CONTROLE_PESSOA b
	where	a.cd_tipo_controle = b.cd_tipo_controle
	and	a.cd_tipo_controle = nr_seq_controle_p
	and	(b.cd_cgc IS NOT NULL AND b.cd_cgc::text <> '');

	ds_retorno_w := to_char(qt_registros_w);
elsif (ie_tipo_dados_p = 'TP') then
	select  count(*)
	into STRICT	qt_registros_w
	from	TIPO_CONTROLE_PESSOA a,
		CONTROLE_PESSOA b
	where	a.cd_tipo_controle = b.cd_tipo_controle
	and	a.cd_tipo_controle = nr_seq_controle_p;

	ds_retorno_w := to_char(qt_registros_w);
elsif (ie_tipo_dados_p = 'DS') then
	select  substr(ds_tipo_controle,1,255)
	into STRICT	ds_retorno_w
	from	TIPO_CONTROLE_PESSOA a
	where	a.cd_tipo_controle = nr_seq_controle_p;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
