SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_convenio (nr_seq_convenio_p text, ie_tipo_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
ds_senha_w	varchar(100);

BEGIN

/*
S -> Senha
*/
ds_retorno_w := '';

if (nr_seq_convenio_p IS NOT NULL AND nr_seq_convenio_p::text <> '') then
	select 	substr(ds_senha,1,255)
	into STRICT	ds_senha_w
	from	convenio
	where	cd_convenio = nr_seq_convenio_p;

	if (ie_tipo_p = 'S') then
		ds_retorno_w := ds_senha_w;
	end if;
end if;


return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
