CREATE OR REPLACE FUNCTION obter_dados_conv_estab (cd_estabelecimento_p bigint, cd_convenio_p bigint, ie_opcao_p bigint) RETURNS varchar AS $body$
DECLARE

/* 
ie_opcao_p: 
1 - Permite utilizar opção Gerar NF (desdobrar por contas) 
2 - Tomador NF Desdob 
3 - Buscar cd conta contabáil convenio estab. 
4 - Buscar a quantidade de horas do início do ciclo 
*/
 
ds_retorno_w	varchar(4000);


BEGIN 
 
if (ie_opcao_p = 1) then				 
	select	coalesce(ie_gerar_nf_desdob,'N') 
	into STRICT	ds_retorno_w 
	from	convenio_estabelecimento 
	where	cd_estabelecimento 	= cd_estabelecimento_p 
	and	cd_convenio 		= cd_convenio_p;
elsif (ie_opcao_p = 2) then 
	select	coalesce(ie_tomador_nf_desdob,'P') 
	into STRICT	ds_retorno_w 
	from	convenio_estabelecimento 
	where	cd_estabelecimento 	= cd_estabelecimento_p 
	and	cd_convenio 		= cd_convenio_p;
elsif (ie_opcao_p = 3) then 
	select	max(cd_conta_contabil) 
	into STRICT	ds_retorno_w 
	from	convenio_estabelecimento 
	where	cd_estabelecimento 	= cd_estabelecimento_p 
	and	cd_convenio 		= cd_convenio_p;
elsif (ie_opcao_p = 4) then 
	select	coalesce(qt_horas_inicio_ciclo,0) 
	into STRICT	ds_retorno_w 
	from	convenio_estabelecimento 
	where	cd_estabelecimento 	= cd_estabelecimento_p 
	and	cd_convenio 		= cd_convenio_p;
end if;
 
return ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
