CREATE OR REPLACE FUNCTION obter_dados_conv_mapa_dieta (nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE

 
ds_Retorno_w				varchar(240);
nr_atendimento_w			bigint;
cd_setor_atendimento_w		integer;
cd_unidade_basica_w			varchar(10);
cd_unidade_compl_w			varchar(10);
nr_acompanhante_w			smallint;
qt_dieta_acomp_w			smallint;
ds_lib_dieta_w				varchar(60);
cd_convenio_w				integer;


BEGIN 
 
select	nr_atendimento, 
		cd_setor_atendimento, 
		cd_unidade_basica, 
		cd_unidade_compl 
into STRICT	nr_atendimento_w, 
		cd_setor_atendimento_w, 
		cd_unidade_basica_w, 
		cd_unidade_compl_w 
from	mapa_dieta 
where	nr_sequencia	= nr_sequencia_p;
 
if (coalesce(nr_atendimento_w::text, '') = '') then 
	select	nr_atendimento 
	into STRICT	nr_atendimento_w 
	from	unidade_atendimento 
	where	cd_setor_atendimento	= cd_setor_atendimento_w 
	and	cd_unidade_basica	= cd_unidade_basica_w 
	and	cd_unidade_compl	= cd_unidade_compl_w;
end if;
 
select	Obter_Convenio_Atendimento(nr_atendimento_w) 
into STRICT	cd_convenio_w
;
 
select	coalesce(a.nr_acompanhante,0), 
	a.qt_dieta_acomp, 
	substr(obter_valor_dominio(1396,a.ie_lib_dieta),1,60) 
into STRICT	nr_acompanhante_w, 
	qt_dieta_acomp_w, 
	ds_lib_dieta_w 
from	atend_categoria_convenio a 
where	nr_seq_interno	= obter_atepacu_paciente(nr_atendimento_w,'IAA');
 
if (nr_acompanhante_w > 0) then 
	ds_retorno_w	:= 'Nº acomp: '||nr_acompanhante_w ||obter_desc_expressao(293685)/*' Nº acomp dieta lib: '*/||qt_dieta_acomp_w ||obter_desc_expressao(287910)/*' Dieta lib: '*/||': '||ds_lib_dieta_w;
end if;
 
RETURN ds_retorno_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
