SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_cot_compra_forn ( nr_cot_compra_p bigint, nr_seq_fornecedor_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
/*ie opção 
CNPJ	= CNPJ do fornecedor 
F	= Tipo de frete 
DF	= Descrição do Tipo de frete 
S	= Status 
VLM	= Valor mínimo nota fiscal 
CPG	= Condição de pagamento 
NMF	= Nome do fornecedor 
CDP	= Codigo pessoa física 
DE	= Dias entrega 
*/
 
			 
ds_retorno_w		varchar(80);
cd_cgc_fornecedor_w	varchar(60);
ie_frete_w		varchar(1);
ds_frete_w		varchar(80);
ie_status_w		varchar(5);
vl_minimo_nf_w		double precision;
cd_condicao_pagamento_w	bigint;
ds_condicao_pagamento_w	varchar(80);
nm_fornecedor_w		varchar(80);
cd_pessoa_fisica_w	varchar(10);
qt_dias_entrega_w		integer;


BEGIN 
 
if (nr_cot_compra_p IS NOT NULL AND nr_cot_compra_p::text <> '') and (nr_seq_fornecedor_p IS NOT NULL AND nr_seq_fornecedor_p::text <> '') then 
	select	cd_cgc_fornecedor, 
		ie_frete, 
		substr(obter_valor_dominio(87, ie_frete),1,80), 
		ie_status, 
		coalesce(obter_dados_pf_pj(cd_pessoa_fisica, cd_cgc_fornecedor, 'VL'), 0), 
		cd_condicao_pagamento, 
		substr(obter_desc_cond_pagto(cd_condicao_pagamento),1,80), 
		substr(obter_nome_pf_pj(cd_pessoa_fisica, cd_cgc_fornecedor),1,80), 
		cd_pessoa_fisica, 
		qt_dias_entrega 
	into STRICT	cd_cgc_fornecedor_w, 
		ie_frete_w, 
		ds_frete_w, 
		ie_status_w, 
		vl_minimo_nf_w, 
		cd_condicao_pagamento_w, 
		ds_condicao_pagamento_w, 
		nm_fornecedor_w, 
		cd_pessoa_fisica_w, 
		qt_dias_entrega_w 
	from	cot_compra_forn 
	where	nr_cot_compra = nr_cot_compra_p 
	and	nr_sequencia = nr_seq_fornecedor_p;
 
	if (ie_opcao_p = 'CNPJ') then 
		ds_retorno_w := cd_cgc_fornecedor_w;
	elsif (ie_opcao_p = 'F') then 
		ds_retorno_w := ie_frete_w;
	elsif (ie_opcao_p = 'DF') then 
		ds_retorno_w := ds_frete_w;
	elsif (ie_opcao_p = 'S') then 
		ds_retorno_w := ie_status_w;
	elsif (ie_opcao_p = 'VLM') then 
		ds_retorno_w := vl_minimo_nf_w;
	elsif (ie_opcao_p = 'CPG') then 
		ds_retorno_w := cd_condicao_pagamento_w;
	elsif (ie_opcao_p = 'NMF') then 
		ds_retorno_w := nm_fornecedor_w;
	elsif (ie_opcao_p = 'CDP') then 
		ds_retorno_w := cd_pessoa_fisica_w;
	elsif (ie_opcao_p = 'DCP') then 
		ds_retorno_w := ds_condicao_pagamento_w;
	elsif (ie_opcao_p = 'DE') then 
		ds_retorno_w := qt_dias_entrega_w;
	end if;
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
