SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_cot_compra ( nr_cot_compra_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/*ie opção
D = Data da Cotação
C = Código Comprador
N = Nome Comprador
S = Código Pessoa Solicitante
P = Nome Pessoa Solicitante
R = Data Retorno Precisto
E - Número documento externo*/
ds_retorno_w				varchar(60);
dt_cot_compra_w			timestamp;
cd_comprador_w			varchar(10);
cd_pessoa_solicitante_w		varchar(10);
dt_retorno_prev_w			timestamp;
nm_pessoa_solicitante_w		varchar(60);
nm_comprador_w			varchar(60);
nr_seq_motivo_cancel_w		bigint;
nr_documento_externo_w		varchar(10);


BEGIN

if (nr_cot_compra_p IS NOT NULL AND nr_cot_compra_p::text <> '') then
	select	dt_cot_compra,
		cd_comprador,
		substr(obter_nome_pf(cd_comprador),1,60) nm_comprador,
		cd_pessoa_solicitante,
		substr(obter_nome_pf(cd_pessoa_solicitante),1,60) nm_pessoa_solicitante,
		dt_retorno_prev,
		nr_seq_motivo_cancel,
		nr_documento_externo
	into STRICT	dt_cot_compra_w,
		cd_comprador_w,
		nm_comprador_w,
		cd_pessoa_solicitante_w,
		nm_pessoa_solicitante_w,
		dt_retorno_prev_w,
		nr_seq_motivo_cancel_w,
		nr_documento_externo_w
	from	cot_compra
	where	nr_cot_compra = nr_cot_compra_p;

	if (ie_opcao_p = 'D') then
		ds_retorno_w := dt_cot_compra_w;
	elsif (ie_opcao_p = 'C') then
		ds_retorno_w := cd_comprador_w;
	elsif (ie_opcao_p = 'N') then
		ds_retorno_w := nm_comprador_w;
	elsif (ie_opcao_p = 'S') then
		ds_retorno_w := cd_pessoa_solicitante_w;
	elsif (ie_opcao_p = 'P') then
		ds_retorno_w := nm_pessoa_solicitante_w;
	elsif (ie_opcao_p = 'R') then
		ds_retorno_w := dt_retorno_prev_w;
	elsif (ie_opcao_p = 'M') then
		ds_retorno_w := nr_seq_motivo_cancel_w;
	elsif (ie_opcao_p = 'E') then
		ds_retorno_w := nr_documento_externo_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
