CREATE OR REPLACE FUNCTION obter_dados_cpi_solic ( nr_seq_solic_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(20);

/*

DTD - Data de Desejada

*/
BEGIN

select	to_char(dt_desejada,'dd/mm/yyyy hh24:mi:ss')
into STRICT	ds_retorno_w
from	same_cpi_solic
where	nr_sequencia = nr_seq_solic_p;

return	ds_retorno_w;

end	;
