CREATE OR REPLACE FUNCTION obter_dados_dcb ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);
cd_dcb_w	varchar(20);


BEGIN

if (nr_sequencia_p > 0) then
	select	cd_dcb
	into STRICT	cd_dcb_w
	from	dcb_medic_controlado
	where	nr_sequencia	= nr_sequencia_p;
end if;

if (ie_opcao_p = 'C') then
	ds_retorno_w := cd_dcb_w;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
