CREATE OR REPLACE FUNCTION obter_dados_dctf_lote_darf ( nr_seq_dctf_darf_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [  ] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
-------------------------------------------------------------------------------------------------------------------
Referências:
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w			varchar(4000);
ie_agrupar_titulos_w		dctf_lote.ie_agrupar_titulos%type;


BEGIN
if (nr_seq_dctf_darf_p IS NOT NULL AND nr_seq_dctf_darf_p::text <> '') then
	if (ie_opcao_p = 'J') then
		select	coalesce(a.ie_agrupar_titulos, 'S')
		into STRICT	ie_agrupar_titulos_w
		from	dctf_lote	a,
			dctf_lote_darf	b
		where	a.nr_sequencia	= b.nr_seq_lote
		and	b.nr_sequencia	= nr_seq_dctf_darf_p;

		if (ie_agrupar_titulos_w = 'S') then
			select	coalesce(b.vl_juros,0)
			into STRICT	ds_retorno_w
			from	darf		b,
				dctf_lote_darf	a
			where	b.nr_sequencia	= a.nr_seq_darf
			and	a.nr_sequencia	= nr_seq_dctf_darf_p;
		else
			select	sum(c.vl_saldo_juros)
			into STRICT	ds_retorno_w
			from	titulo_pagar			c,
				darf_titulo_pagar		b,
				dctf_lote_darf			a
			where	b.nr_seq_darf	= a.nr_seq_darf
			and	c.nr_titulo	= b.nr_titulo
			and	a.nr_sequencia	= nr_seq_dctf_darf_p;
		end if;

		ds_retorno_w	:= campo_mascara_virgula(ds_retorno_w);
	elsif (ie_opcao_p = 'M') then
		select	coalesce(a.ie_agrupar_titulos, 'S')
		into STRICT	ie_agrupar_titulos_w
		from	dctf_lote	a,
			dctf_lote_darf	b
		where	a.nr_sequencia	= b.nr_seq_lote
		and	b.nr_sequencia	= nr_seq_dctf_darf_p;

		if (ie_agrupar_titulos_w = 'S') then
			select	coalesce(b.vl_multa,0)
			into STRICT	ds_retorno_w
			from	darf		b,
				dctf_lote_darf	a
			where	b.nr_sequencia	= a.nr_seq_darf
			and	a.nr_sequencia	= nr_seq_dctf_darf_p;
		else
			select	sum(c.vl_saldo_multa)
			into STRICT	ds_retorno_w
			from	titulo_pagar			c,
				darf_titulo_pagar		b,
				dctf_lote_darf			a
			where	b.nr_seq_darf	= a.nr_seq_darf
			and	c.nr_titulo	= b.nr_titulo
			and	a.nr_sequencia	= nr_seq_dctf_darf_p;
		end if;

		ds_retorno_w	:= campo_mascara_virgula(ds_retorno_w);
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
