SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION obter_dados_descricao_cir ( nr_seq_prot_cirurgia_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_liberacao_w		varchar(255);
ds_tipo_descricao_w	varchar(255);
nm_usuario_w		varchar(255);

/*
ie_opcao_p = 'L' - Data de liberação
ie_opcao_p = 'T' - Tipo da descrição
ie_opcao_p = 'U' - Usuário liberação
*/
BEGIN

select	to_char(dt_liberacao,'dd/mm/yyyy hh24:mi:ss'),
	substr(obter_valor_dominio(1424,ie_tipo_descricao),1,60),
	nm_usuario
into STRICT	dt_liberacao_w,
	ds_tipo_descricao_w,
	nm_usuario_w
from 	cirurgia_descricao
where	nr_sequencia = nr_seq_prot_cirurgia_p;

if (ie_opcao_p = 'L') then
	return dt_liberacao_w;
elsif (ie_opcao_p = 'T') then
	return ds_tipo_descricao_w;
elsif (ie_opcao_p = 'U') then
	if (coalesce(dt_liberacao_w::text, '') = '') then
		return 	null;
	else
		return 	nm_usuario_w;
	end if;
end if;

end	;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION obter_dados_descricao_cir ( nr_seq_prot_cirurgia_p bigint, ie_opcao_p text) FROM PUBLIC;

